    tex_files = [MAIN_TEX]
    if os.path.exists(SECTIONS_DIR):
        for f in os.listdir(SECTIONS_DIR):
            if f.endswith('.tex'):
                tex_files.append(os.path.join(SECTIONS_DIR, f))
